{
    "openapi": "3.0.1",
    "info": {
        "title": "demo",
        "description": "Demo application with hexagonal architecture and DDD approach",
        "contact": {
            "name": "DevTeam",
            "email": "exampleemail@gmail.com"
        },
        "license": {
            "name": "Example",
            "url": "https://www.example.com"
        },
        "version": "0.0.1-SNAPSHOT"
    },
    "servers": [
        {
            "url": "http://localhost:8080/api/rest",
            "description": "Generated server url"
        }
    ],
    "tags": [
        {
            "name": "Price Filter",
            "description": "Price Filter API"
        }
    ],
    "paths": {
        "/v1/price-filter": {
            "post": {
                "tags": [
                    "Price Filter"
                ],
                "summary": "Filter prices",
                "description": "Filter prices by brandId, productId and applicationDate",
                "operationId": "filterPrices",
                "requestBody": {
                    "description": "Filter request input",
                    "content": {
                        "application/json": {
                            "schema": {
                                "$ref": "#/components/schemas/FilterInput"
                            }
                        }
                    },
                    "required": true
                },
                "responses": {
                    "200": {
                        "description": "OK",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/PriceOutput"
                                }
                            }
                        }
                    },
                    "400": {
                        "description": "Bad Request",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/ProblemDetail"
                                }
                            }
                        }
                    },
                    "404": {
                        "description": "Not Found",
                        "content": {
                            "application/json": {}
                        }
                    },
                    "500": {
                        "description": "Internal Server Error",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/ProblemDetail"
                                }
                            }
                        }
                    }
                }
            }
        }
    },
    "components": {
        "schemas": {
            "FilterInput": {
                "required": [
                    "applicationDate",
                    "brandId",
                    "productId"
                ],
                "type": "object",
                "properties": {
                    "applicationDate": {
                        "type": "string",
                        "description": "Application date",
                        "format": "date-time"
                    },
                    "productId": {
                        "minimum": 1,
                        "type": "integer",
                        "description": "Product identifier",
                        "format": "int32",
                        "example": 35455
                    },
                    "brandId": {
                        "minimum": 1,
                        "type": "integer",
                        "description": "Brand identifier",
                        "format": "int32",
                        "example": 1
                    }
                },
                "description": "Filter request input"
            },
            "PriceOutput": {
                "required": [
                    "brandId",
                    "endDate",
                    "price",
                    "priceList",
                    "productId",
                    "startDate"
                ],
                "type": "object",
                "properties": {
                    "productId": {
                        "type": "integer",
                        "description": "Product identifier",
                        "format": "int32",
                        "example": 35455
                    },
                    "brandId": {
                        "type": "integer",
                        "description": "Brand identifier",
                        "format": "int32",
                        "example": 1
                    },
                    "priceList": {
                        "type": "integer",
                        "description": "Applicable pricing rate identifier",
                        "format": "int32",
                        "example": 35455
                    },
                    "startDate": {
                        "type": "string",
                        "description": "date range in which the indicated price rate applies.",
                        "format": "date-time"
                    },
                    "endDate": {
                        "type": "string",
                        "description": "date range in which the indicated price rate applies.",
                        "format": "date-time"
                    },
                    "price": {
                        "type": "number",
                        "description": "Price",
                        "format": "double",
                        "example": 30.5
                    }
                },
                "description": "Price Output"
            },
            "ProblemDetail": {
                "type": "object",
                "properties": {
                    "type": {
                        "type": "string",
                        "format": "uri"
                    },
                    "title": {
                        "type": "string"
                    },
                    "status": {
                        "type": "integer",
                        "format": "int32"
                    },
                    "detail": {
                        "type": "string"
                    },
                    "instance": {
                        "type": "string",
                        "format": "uri"
                    },
                    "properties": {
                        "type": "object",
                        "additionalProperties": {
                            "type": "object"
                        }
                    }
                }
            }
        }
    }
}
